mapper.select:
select username, userid, email, phone, address 
from member
where userid = #{userid}
and userpwd = #{userpwd}

select userpwd
from member
where userid = #{userid}
and email = #{email}

mapper.update:
update member
set userpwd = #{userpwd}, username = #{username}, email = #{email}, phone = #{phone}, address = #{address}
where userid = #{userid}
